import React from 'react';
import { useOutletContext, useParams } from 'react-router-dom';
const api = 'http://localhost:3000/data';

function Delete() {
  const {
    fullnames, 
    listNames, //is an array 
    setFullNames, 
    searchPerson, 
    setSearchPerson 
  } = useOutletContext();
  const { id } = useParams();

  console.log(listNames)





  function listeningEventTargetValue(e) {
    setSearchPerson(e.target.value);
  }

  const handleDelete = ()=> {
    fetch(`${api}/${id}`, {
      method: 'DELETE',
    })
      .then((res) => res.json())
      .then((deletedItem) => {
        const updatedList = listNames.filter(
          (item) => item.id !== deletedItem.id
        );
        setFullNames(updatedList);
      })
      .catch((error) => console.log(error));
  };


    const buttonWithDelete = ()=> fullnames.map((item)=>(
      <ol key={item.id}>
        <button onClick={()=>handleDelete(item.id)}>{item.lastname}</button>
      </ol>
    ))

console.log(buttonWithDelete)


  return (
    <>
      <h4>Click on name to delete!</h4>
      <input
        type="text"
        placeholder="search person"
        onChange={listeningEventTargetValue}
      />
      <br />
      <br />
      <div id="glass">
        <ul>
          {buttonWithDelete}
        </ul>
      </div>
    </>
  );
}

export default Delete;
